{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"9taller.py","provenance":[],"collapsed_sections":[],"authorship_tag":"ABX9TyOwSA9TXhWopwIfwn0XVzmU"},"kernelspec":{"name":"python3","display_name":"Python 3"}},"cells":[{"cell_type":"code","metadata":{"id":"TvwXQZt_dTVL","colab_type":"code","colab":{}},"source":["import matplotlib.pyplot as plt\n","import numpy as np\n","import pandas as pd\n","import statistics as st\n","\n","datos=pd.read_csv('Saber_Pro.csv')\n","\n","#print(datos)\n","\n","b=datos['RESULT_DESEMPENO']\n","\n","#Histograma\n","plt.hist(b)\n","plt.show()\n","c=datos['RESULT_PUNTAJE']\n","\n","#Media\n","print(c.mean())\n","\n","#Mediana (datos no agrupados)\n","print(st.median(c))\n","\n","#Mediana (datos agrupados)\n","print(st.median_grouped(c))\n","\n","#Moda\n","print(st.mode(c))\n","\n","#Rango\n","def rango(x):\n","    return max(x)-min(x)\n","\n","print(rango(c))\n","\n","#Varianza de una población\n","print(st.pvariance(c))\n","\n","#Varianza de una muestra\n","print(st.variance(c))\n","\n","#Desviacion estandar\n","print(np.std(c))\n","\n","#Covarianza\n","print(np.cov(c,b))\n","\n","#Correlacion\n","print(np.corrcoef(c,b))\n","\n","#plotting no se sobrescriba\n","#a=np.array[datos['RESULT_DESEMPENO'][datos['RESULT_DESEMPENO']]==1.count()\n","a2=np.array([datos['RESULT_DESEMPENO'][datos['RESULT_DESEMPENO']==1].count(),datos['RESULT_DESEMPENO'][datos['RESULT_DESEMPENO']==2].count(),datos['RESULT_DESEMPENO'][datos['RESULT_DESEMPENO']==3].count(),datos['RESULT_DESEMPENO'][datos['RESULT_DESEMPENO']==4].count()])\n","data = {' 1':a2[0], '2':a2[1], '3':a2[2], '4':a2[3]}\n","names = list(data.keys())\n","values = list(data.values())\n","\n","fig, axs = plt.subplots(1, 2, figsize=(9, 3), sharey=True)\n","axs[0].bar(names, values)\n","axs[1].scatter(names, values)\n","fig.suptitle(\"resultado vs Desempeño\")\n","\n","\n","fig = plt.figure(figsize=(6, 6))\n","ax = fig.add_axes([0.1, 0.1, 0.8, 0.8])\n","\n","labels = 'desempeño 1', 'desempeño 2', 'desempeño 3', 'desempeño 4'\n","fracs = [a2[0]*100/b.size,a2[1]*100/b.size,a2[2]*100/b.size,a2[3]*100/b.size]\n","print(fracs)\n","pies = ax.pie(fracs, labels=labels, autopct='%1.1f%%')"],"execution_count":0,"outputs":[]},{"cell_type":"markdown","metadata":{"id":"HiJxMG8rTDai","colab_type":"text"},"source":["mathplolib.org ejemplos"]}]}